    public static void calculateSantaSteps() {
        int floorCount = 0;
        char upFloor = '(';
        char downFloor = ')';
        int counter = 0;
        for (Character charItem : stairInput.toCharArray()) {
            char item = charItem;
            if (item == upFloor) {
                floorCount++;
            } else if(item == downFloor) {
                floorCount--;
            }

            if(floorCount == -1){
                System.out.println("Position is : " + (counter+1));
                break;
            }
            counter++;
        }
        System.out.println("Santa Floor Level: " + floorCount);
    }

    public static void main(String[] args) {
        calculateSantaSteps();
    }

